package ejercicio2;

public class ejercicioDos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1;
		int y = 2;
		double n = 4.7;
		double m = 3.2;
		
		
		System.out.println("El valor de la variable x es: "+ x);
		System.out.println("El valor de la variable y es: "+ y);
		System.out.println("El valor de la variable n es: "+ n);
		System.out.println("El valor de la variable m es: "+ m);
		System.out.println(x + " + " + y + " = " + (x+y));
		System.out.println(x + " - " + y + " = " + (x-y));
		System.out.println(x + " x " + y + " = " + (x*y));
		System.out.println(x + " / " + y + " = " + (x/y));
		System.out.println("El resto de la division entre "+ x +" e "+ y +" es igual a "+ (x%y));
		System.out.println(n + " + " + n + " = " + (n+m));
		System.out.println(n + " - " + n + " = " + (n-m));
		System.out.println(n + " x " + m + " = " + (n*m));
		System.out.println(n + " / " + m + " = " + (n/m));
		System.out.println("El resto de la division entre "+ n +" y "+ m +" es igual a "+ (n%m));
		System.out.println(x + " + " + n + " = " + (x+n));
		System.out.println(y + " / " + m + " = " + (y/m));
		System.out.println("El resto de la division entre "+ y +" y "+ m +" es igual a "+ (y%m));
		System.out.println("El doble de x es: "+ (x*2));
		System.out.println("El doble de y es: "+ (y*2));
		System.out.println("El doble de m es: "+ (m*2));
		System.out.println("El doble de n es: "+ (n*2));
		System.out.println(x +" + "+ y +" + " + n +" + "+ m + " = " +(x+y+n+m));
		System.out.println(x +" x "+ y +" x " + n +" x "+ m + " = " +(x*y*n*m));
	
		
		
		
		
	}
	
}
